;
COMMENT ON COLUMN T_PRINCIPAL_POLICY.TX_DESC      IS 'Description of policy'
;


COMMENT ON TABLE T_PRINCIPALS IS 'This is internal table and stores identifiers for product catalog entities. (Package:Misc. Feature)'
;



