configdata.Program:
CREATE PROCEDURE SetDefaults
  @namespace VARCHAR OUTPUT
  @_ProductViewID INTEGER OUTPUT
  @_PriceableItemName VARCHAR OUTPUT
  @TransactionTime DATETIME 
  @_Timestamp DATETIME  OUTPUT
as
	BEGIN
  set @namespace = 'mt'
  set @_ProductViewID=CAST(#metratech.com/ldperfSimplePV# as INTEGER)
	set @_PriceableItemName = 'ldperfSimplePI'

	IF NOT (@_Timestamp IS NULL)
		set @_Timestamp = @TransactionTime

	END
