applicabilityrule.formula:

CREATE PROCEDURE ApplicabilityRule
--inputs
@FeatureType ENUM
--outputs
@IsApplicable BOOLEAN OUTPUT
AS
DECLARE @Record ENUM
SET @Record = #metratech.com/audioconffeature/FeatureType/Record#
SET @IsApplicable = CASE WHEN (@FeatureType = @Record) THEN TRUE ELSE FALSE END;
