configdata.Program:

CREATE PROCEDURE calcoverusedportcharges 	@OverusedChargeType						ENUM -- $ICE:('metratech.com/audioconfcommon','ChargeType')$
																					@NumOverusedPorts							INTEGER
																					@OverusedPortRate	      			DECIMAL
																					@OverusedPortApplicBool				BOOLEAN
																					@CancelledFlag										VARCHAR
																					@OverusedPortCharges 					DECIMAL OUTPUT
AS
DECLARE @strChargeType VARCHAR;
SET @strChargeType = CAST(@OverusedChargeType AS VARCHAR)
SET @OverusedPortCharges = 0.0;
IF @OverusedPortApplicBool= FALSE OR @CancelledFlag = 'Y' OR @CancelledFlag = 'y'
	RETURN;
SET @OverusedPortCharges =
CASE
	WHEN (@strChargeType = 'Fixed' AND @NumOverusedPorts > 0)
		THEN @OverusedPortRate
	WHEN (@strChargeType = 'PerPort')
		THEN  @OverusedPortRate * CAST(@NumOverusedPorts AS DECIMAL)
	ELSE 0.0
END;
