configdata.Program:

	CREATE PROCEDURE calcrawduration 
		@ActualStartTime DATETIME 
		@ConferenceEndTime DATETIME  
		@RawActualDuration INTEGER OUTPUT
  AS
		DECLARE @timeConnectTime TIME;
		DECLARE @timeDisconnectTime TIME;
		DECLARE @intConnectTime INTEGER;
		DECLARE @intDisconnectTime INTEGER;
		SET @timeConnectTime = CAST (@ActualStartTime AS TIME);
		SET @timeDisconnectTime = CAST (@ConferenceEndTime AS TIME);
		SET @intConnectTime = CAST (@timeConnectTime AS INTEGER);
		SET @intDisconnectTime = CAST (@timeDisconnectTime AS INTEGER);
		IF (@intDisconnectTime < @intConnectTime)
				RETURN;
		SET @RawActualDuration = @intDisconnectTime - @intConnectTime;
