configdata.Program:

CREATE PROCEDURE calcreservecharges @ScheduledConnections INTEGER
																		@ReserveRate	DECIMAL
																		@ReserveChargeType 	ENUM -- $ICE:('metratech.com/audioconfcommon','ChargeType')$
																		@ReserveApplicBool	BOOLEAN
																		@ReservationCharges	DECIMAL OUTPUT
AS
DECLARE @strChargeType VARCHAR;
SET @strChargeType = CAST(@ReserveChargeType AS VARCHAR)
SET @ReservationCharges = 0.0;
IF (@ReserveApplicBool = FALSE)
	RETURN;
SET @ReservationCharges = 
	CASE
		WHEN (@strChargeType = 'Fixed')
			THEN @ReserveRate
		WHEN (@strChargeType = 'PerPort')
			THEN @ReserveRate * CAST(@ScheduledConnections AS DECIMAL)
	END;
