configdata.Program:

			CREATE PROCEDURE calcunderusedportcharges 	@UnusedChargeType						ENUM -- $ICE:('metratech.com/audioconfcommon','ChargeType')$
																									@NumUnusedPorts							INTEGER
																									@UnusedPortRate	      			DECIMAL
																									@UnusedPortApplicBool				BOOLEAN
																									@CancelledFlag									VARCHAR
																									@UnusedPortCharges 					DECIMAL OUTPUT
			AS
			DECLARE @strChargeType VARCHAR;
			SET @strChargeType = CAST(@UnusedChargeType AS VARCHAR)
			SET @UnusedPortCharges = 0.0;
			IF @UnusedPortApplicBool= FALSE OR @CancelledFlag = 'Y' OR @CancelledFlag = 'y'
				RETURN;
			SET @UnusedPortCharges =
			CASE
				WHEN (@strChargeType = 'Fixed' AND @NumUnusedPorts > 0)
					THEN 
          @UnusedPortRate
				WHEN (@strChargeType = 'PerPort')
					THEN  @UnusedPortRate * CAST(@NumUnusedPorts AS DECIMAL)
				ELSE 0.0
			END;
