configdata.Program:

CREATE PROCEDURE calcports 						 @ScheduledConnections 				INTEGER
																				@ActualNumConnections 				INTEGER 
																				@UserNumConnections 					INTEGER 
																				@UnusedExcludeRoles 					BOOLEAN 
																				@UnusedPortChargeDef 				 ENUM --$ICE:('metratech.com/audioconfcommon','PortChargeDef')$
																				@UnusedThresholdFree 				 INTEGER
																				@UnusedAdvancedPercentage 		INTEGER
																				@UnusedPortDelta 						 INTEGER
																				@NumUnusedPorts 							INTEGER OUTPUT
AS
DECLARE @PortsNotUsed INTEGER;
DECLARE @AdvancedCalc INTEGER;
DECLARE @NumUsedPorts INTEGER;
DECLARE @PortsUseType VARCHAR;
DECLARE @strChargeDef VARCHAR;
DECLARE @NumConnections INTEGER;
SET @strChargeDef = CAST(@UnusedPortChargeDef AS VARCHAR);
SET @NumUnusedPorts = 0; 
SET @PortsUseType = CASE WHEN (@ActualNumConnections > @ScheduledConnections) THEN 'Overused' ELSE 'Underused' END;
IF @PortsUseType = 'Overused'
	RETURN;
PRINT 'UnusedExcludeRoles';
PRINT CAST(@UnusedExcludeRoles AS VARCHAR);
PRINT 'UserNumConnections';
PRINT CAST(@UserNumConnections AS VARCHAR);
PRINT 'ActualNumConnections';
PRINT CAST(@ActualNumConnections AS VARCHAR);
SET @NumConnections = CASE WHEN (@UnusedExcludeRoles) THEN @UserNumConnections ELSE @ActualNumConnections END;
PRINT 'Connections used (for overused ports calculation)';
PRINT CAST(@NumConnections AS VARCHAR);
SET @PortsNotUsed = @ScheduledConnections - @NumConnections;
	PRINT CAST(@PortsNotUsed AS VARCHAR)
SET @AdvancedCalc = (@ScheduledConnections * @UnusedAdvancedPercentage / 100) - @NumConnections;
SET @NumUnusedPorts = 
	CASE
		WHEN (@strChargeDef = 'Simple')
			THEN @PortsNotUsed
		WHEN @strChargeDef = 'Threshold'
			THEN @PortsNotUsed - @UnusedThresholdFree
		WHEN @strChargeDef = 'Advanced' AND @PortsNotUsed > @UnusedPortDelta
			THEN @AdvancedCalc
		ELSE 0
	END;
SET @NumUnusedPorts = CASE WHEN @NumUnusedPorts > 0 THEN @NumUnusedPorts ELSE 0 END;
