configdata.Program:

CREATE PROCEDURE setdefaultservicelevel	
	@ServiceLevel	ENUM OUTPUT -- $ICE:('metratech.com/audioconfcommon','ServiceLevel')$
	@SystemName VARCHAR OUTPUT
AS
DECLARE @strServiceLevel VARCHAR;
SET @strServiceLevel = 'metratech.com/audioconfcommon/ServiceLevel/Standard';
SET @ServiceLevel = CAST (@strServiceLevel AS ENUM);
SET @SystemName = 'Bridge1';
