configdata.Program:
CREATE PROCEDURE InsertTaxInclusive
@_TaxInclusive VARCHAR(1)
@_TaxInformational  VARCHAR(1)
@_TaxCalculated VARCHAR(1)
as

if (@_TaxInclusive is null)
		set @_TaxInclusive = 'N';
if (@_TaxInformational is null)
		set @_TaxInformational = 'N';
if (@_TaxCalculated is null)
    set @_TaxCalculated = 'N';
