configdata.Program:
CREATE PROCEDURE uomnum @MTIUOM ENUM -- $ICE:('metratech.com/audioconffeature','FeatureUOM')$ @UOMNUM INTEGER OUTPUT AS DECLARE @strUOM VARCHAR;
SET @strUOM = CAST (@MTIUOM AS VARCHAR);
SET @UOMNUM = CASE WHEN @strUOM = 'Seconds' THEN 1 WHEN @strUOM = 'Minutes' THEN 60 WHEN @strUOM = 'Hours' THEN 3600 WHEN @strUOM = 'Days' THEN 86400 ELSE 0 -- Error END
